import operator
class Solution(object):

    def threeSum(self, nums):

        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        nums = sorted(nums)
        # print(nums)
        length = len(nums)
        res = []
        count = 0
        for i in range(length):
            p1 = 0
            p2 = length - 1
            if (nums[i] == 0):
                count = count +1
                if p2==count:
                    print("数组全是0")
                    break
            while p1 < i < p2:
                num = nums[i] + nums[p1] + nums[p2]
                # if (nums[p1] == 0) and (nums[p2] == 0) and (nums[i]==0):
                #     break
                #全是正或全是负 相加都不可能为0
                if (nums[p1] > 0) or (nums[p2] < 0):
                    break
                if num > 0:
                    p2 -= 1
                elif num < 0:
                    p1 += 1
                else:
                    if [nums[p1], nums[i], nums[p2]] not in res:
                        res.append([nums[p1], nums[i], nums[p2]])
                    else:
                        pass
                    p1 += 1
                    p2 -= 1
        print(count)
        return sorted(res)

    def diff_dupl(self,result):
        """
        两个列表去重
        :return:
        """
        # print(len(result))
        diff_dupl_list=[]
        for i in range(len(result)):
            end = len(result)-i-1
            if (result[i] != result[end]):
                # print(str(result[i]) + "和" + str(result[end]) + "不相等")
                diff_dupl_list.append(result[i])
        return diff_dupl_list

